import React from 'react'
import { useEffect } from 'react';

const EditPost = (props) => {

  useEffect(()=>{
    let message = props.original;
    document.getElementById('updateText').innerHTML = message;
  }, []);

  const closeModal = () =>{
    props.rerender();
  }


  return (
    <div className='modal'>
      <form>
        <h1>Made a Mistake? Edit that shit!</h1>
        <p onClick={closeModal}>Close Modal</p>
        <textarea id='updateText' placeholder='Edit Post Message' />
        <button type='submit'>Edit this post</button>
      </form>


    </div>
  )
}

export default EditPost
