import React, { useState } from 'react'
import { useEffect } from 'react';
import axios from 'axios';

const EditPost = (props) => {

  const [updatedPost, setUpdatePost] = useState({
    newMessage:  props.original,
    id: props.id,
  });

  useEffect(()=>{
    let message = props.original;
    document.getElementById('updateText').innerHTML = message;
  }, []);

  const closeModal = () =>{
    props.rerender();
  }

  const changeHandle = (e) => {
    let value = e.target.value;
    setUpdatePost({...updatedPost, newMessage: value});
  }

  const updatePost = (e) => {
    e.preventDefault();
    axios.post('http://localhost:8888/api/updatePost.php', updatedPost)
     .then((res)=>{
      console.log(res);
      props.upRender(true);
     });
  }


  return (
    <div className='modal'>
      <form>
        <h1>Made a Mistake? Edit that shit!</h1>
        <p onClick={closeModal}>Close Modal</p>
        <textarea id='updateText' placeholder='Edit Post Message' onChange={changeHandle} />
        <button type='submit' onClick={updatePost}>Edit this post</button>
      </form>


    </div>
  )
}

export default EditPost
